export function backendConfiguration({
    pathLoadingGraph,
    pathResource,
    origin,
    id,
    pathPypi,
    pathPyodide,
}: {
    id?: string
    pathLoadingGraph: string
    pathResource: string
    origin?: { secure?: boolean; hostname?: string; port?: number } | string
    pathPypi?: string
    pathPyodide?: string
}): BackendConfiguration {
    if (typeof origin !== 'string') {
        origin = computeOrigin(origin)
    }
    return {
        id,
        origin,
        urlLoadingGraph: `${origin}${pathLoadingGraph}`,
        urlResource: `${origin}${pathResource}`,
        urlPypi: pathPypi ? `${origin}${pathPypi}` : 'https://pypi.org/',
        urlPyodide: pathPyodide
            ? `${origin}${pathPyodide}/$VERSION`
            : `https://cdn.jsdelivr.net/pyodide/v$VERSION/full`,
    }
}

export function errorFactory(error: CdnError): Error {
    if (CircularDependencies.isInstance(error)) {
        return new CircularDependencies(error.detail)
    }
    if (DependenciesError.isInstance(error)) {
        return new DependenciesError(error.detail)
    }
    if (Unauthorized.isInstance(error)) {
        return new Unauthorized(error.detail)
    }
    if (
        'exceptionType' in error &&
        error.exceptionType === 'UpstreamResponseException'
    ) {
        // noinspection TailRecursionJS
        return errorFactory((error as unknown as { detail: CdnError }).detail)
    }
    return error
}

