export async function installViewsModule(): Promise<ViewsModule> {
    const viewsModule = (await setup.installAuxiliaryModule({
        name: 'views',
        cdnClient: webpmClient,
        installParameters: {
            executingWindow: window,
            css: [`${setup.name}#${setup.version}~assets/style.css`],
        },
    })) as unknown as ViewsModule
    viewsModule.Dependencies.webpm = webpmClient
    return viewsModule
}

export async function installWorkersPoolModule(): Promise<WorkersModule> {
    return await setup
        .installAuxiliaryModule({
            name: 'workersPool',
            cdnClient: webpmClient,
            installParameters: {
                executingWindow: window,
            },
        })
        .then((module: WorkersModule) => {
            setupWorkersPoolModule(module)
            return module
        })
}

export function isCdnEvent(event: unknown): event is CdnEvent {
    return eventTypes.includes((event as CdnEvent).step)
}

