    /**
     * Initialize a new instance.
     *
     * @param params
     * @param params.eventsMgr Allow to provide an explicit events manager if needed.
     * @param params.expandedMode Initial expanded mode, default to `auto`
     */
    constructor(
        params: { eventsMgr?: EventsManager; expandedMode?: ExpandedMode } = {},
    ) {
        Object.assign(this, params)
        const header = new InstallHeaderBar({
            eventsMgr: this.eventsMgr,
            mode$: this.mode$,
            expandedMode: params.expandedMode,
        })
        this.children = [
            header,
            {
                tag: 'div',
                class: attr$({
                    source$: header.expanded$,
                    vdomMap: (expanded) =>
                        expanded ? 'p-1 bg-light overflow-auto' : 'd-none',
                }),
                children: [
                    child$({
                        source$: this.mode$,
                        vdomMap: (mode) => {
                            if (mode === 'logs') {
                                return new LogsView({
                                    logEvent$: this.eventsMgr.consoleEvent$,
                                })
                            }
                            return {
                                tag: 'div',
                                children: [
                                    new LoadingGraphEventsView(this.eventsMgr),
                                    new EsmEventsView(this.eventsMgr),
                                    new PyEventsView(this.eventsMgr),
                                    new BackendEventsView(this.eventsMgr),
                                    new CssEventsView(this.eventsMgr),
                                    child$({
                                        source$:
                                            this.eventsMgr.installDoneEvent$,
                                        vdomMap: (ev) => {
                                            const text =
                                                ev.step === 'InstallDoneEvent'
                                                    ? 'All set, environment ready.'
                                                    : 'Error occurred during installation.'
                                            return new FooterView({ text })
                                        },
                                    }),
                                ],
                            }
                        },
                    }),
                ],
            },
        ]
    }

    onEvent(ev: CdnEvent) {
        this.eventsMgr.event$.next(ev)
    }

    toHTML(): HTMLElement {
        return render(this)
    }
}

export class ItemView implements VirtualDOM<'div'> {
    /**
     * Component's class name for CSS query.
     */
