    public readonly class = `${ItemView.CssSelector} d-flex align-items-center`
    public readonly children: ChildrenLike

    constructor({
        name,
        topic,
        version,
        target,
        phases,
    }: {
        name: string
        topic: Topic
        version?: string
        target?: string
        phases: Phase[]
    }) {
        const icons: Record<PhaseStatus, string> = {
            started: 'webpm-pulse',
            succeeded: 'text-success',
            failed: 'text-danger',
            failed401: 'fas text-danger fa-ban',
            failed404: 'fas text-danger fa-search',
        }
        if (version) {
            name += `#${version}`
        }
        if (target) {
            name += `~${target}`
        }
        this.children = [
            {
                tag: 'img',
                src: imageTopics[topic],
                width: 25,
            },
            { tag: 'div', class: 'mx-2' },
            {
                tag: 'div',
                innerText: name,
            },
            ...phases.map((phase) => {
                return {
                    tag: 'i' as const,
                    class: attr$({
                        source$: phase.source$,
                        vdomMap: (event) => {
                            if (
                                event === 'failed401' ||
                                event === 'failed404'
                            ) {
                                return icons[event]
                            }
                            return `${phase.icon} ${icons[event]}`
                        },
                        wrapper: (d) => `${d} mx-1`,
                    }),
                }
            }),
        ]
    }
}

